import React, { useEffect, useRef } from "react";
import "./styles/App.scss";
import Header from './components/Header';

function App() {

  useEffect(() => { 
    let vh = window.innerHeight * .01;
    document.documentElement.style.setProperty('--vh', `${vh}px`);

    //prevents flashing.
    gsap.to("body", 0, {css: {
      visibility: "visible",
    }});
  });

  return (
    <>
      <Header />
    </>
  );
}

export default App;
