import React, { useEffect, useRef } from "react";
import gsap from 'gsap';
import Banner from '../components/Banner'
import Cases from '../components/Cases'
import IntroOverlay from '../components/IntroOverlay'

const Home = () => {
    const title = useRef(null);

  useEffect( () => {

    //timeline
    const tl = gsap.timeline();

    tl.from(".line span", 1, {
      y: 100,
      ease: "power4.out",
      delay: 1,
      skewY: 7,
      stagger: {
        amount: 0.3
      }
    }).to('.overlay-top', 1.6, {
      height: 0,
      ease: "expo.inOut",
      stagger: 0.4,
    }).to('.overlay-bottom', 1.6, {
      width: 0,
      ease: "expo.inOut",
      delay: -.8,
      stagger: {
        amount: 0.4
      }
    }).to('.intro-overlay', 0, {
      css: { display: "none",}
    }).from('.caseImage img', 1.6, {
      scale: 1.4,
      ease: "expo-inOut",
      delay: -2,
      stagger: {
        amount: 0.4
      }
    });
  }, []);
    return (
       <>
        <IntroOverlay />
        <Banner /> 
        <Cases /> 
       </>
    )
}

export default Home
