import React, { useState, useEffect } from 'react';
import { NavLink, withRouter } from 'react-router-dom';
import { ReactComponent as UpArrow } from '../assets/up-arrow.svg';
import gsap from 'gsap';
import { faBorderNone } from '@fortawesome/free-solid-svg-icons';

let tl = gsap.timeline()

const Header = ({dimentions}) => {
    //want to know when menu is open to trigger animation
    const [menuState, setMenuState] = useState({menuOpened: false});

    useEffect(() => {
        if(menuState.menuOpened === true) {
            //Run open menu animations
            gsap.to('nav', {css: {display: "block"}});
            gsap.to('body', {css: {overflow: "hidden"}});

            tl.to(".App", {
                duration: 1,
                y: dimentions.width <= 654 ? '70vh' : '55vh',
                ease: "expo.inOut"
            }).to(".hamburger-menu span", {
                duration: 0.6,
                delay: -1,
                scaleX: 0,
                transformOrigin: "50% 0%",
                ease: "expo.inOut"
            }).to('#Path_1', {
                duration: 0.4,
                delay: -0.6,
                css: {
                    strokeDashoffset: 10,
                    strokeDasharray: 5
                }
            }).to('#Path_2', {
                duration: 0.4,
                delay: -0.6,
                css: {
                    strokeDashoffset: 10,
                    strokeDasharray: 20
                }
            }).to('#Line_1', {
                duration: 0.4,
                delay: -0.6,
                css: {
                    strokeDashoffset: 40,
                    strokeDasharray: 18
                }
            }).to('#circle', {
                duration: 0.6,
                delay: -0.8,
                css: {
                    strokeDashoffset: 0,
                }
            }).to('.hamburger-menu-close', {
                duration: 0.6,
                delay: -0.8,
                css: {
                    display: "block",
                }
            });
        } else {
            //Run close menu animation
            tl.to('.App', {
                duration: 1,
                y: 0,
                ease: "expo.inOut",
            }).to('#circle', {
                duration: 0.6,
                delay: -0.6,
                css: {
                    strokeDashoffset: -193,
                    strokeDasharray: 227 
                }
            }).to('#Path_1', {
                duration: 0.4,
                delay: -0.6,
                css: {
                    strokeDashoffset: 10,
                    strokeDasharray: 10 
                }
            }).to('#Path_2', {
                duration: 0.4,
                delay: -0.6,
                css: {
                    strokeDashoffset: 10,
                    strokeDasharray: 10
                }
            }).to('#Line_1', {
                duration: 0.4,
                delay: -0.6,
                css: {
                    strokeDashoffset: 40,
                    strokeDasharray: 40 
                }
            }).to('.hamburger-menu-close', {
                duration: 0,
                css: {
                    display: "none",
                }
            }).to('body', {
                css: {
                    overflow: "auto",
                }
            }).to('nav', {
                css: {
                    display: "none",
                }
            }); 
        }
    }, [menuState.menuOpened]);

    return (
        <div className="header">
             <div className="container">
                <div className="row v-center space-between">
                    <div className="logo">
                        <a href="/">SPINELLI.</a>
                    </div>
                    <div className="nav-toggle">
                        <div onClick={() => setMenuState({menuOpened: true})} className="hamburger-menu">
                            <span></span>
                            <span></span>
                        </div>
                        <div onClick={() => setMenuState({menuOpened: false})} className="hamburger-menu-close">
                            <UpArrow />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Header
